public class Solution{

	static Scanner scanner=new Scanner(System.in);

	public static void main(String [] args){
		int n=scanner.nextInt();
		int x1=scanner.nextInt(),y1=scanner.nextInt();
		int x2=scanner.nextInt(),y2=scanner.nextInt();
		int x1f=x1,x2f=x2;
		int y1f=y1,y2f=y2;
		int min=Integer.MAX_VALUE;
		for(int i=0;i<2;i++){
			for(int j=0;j<2;j++){
				int t=0;
				if(x1==x2 && x1==0)t+=n;
				else if(x1==x2 && x2==n)t+=(n*2);
				else if(x1!=x2 && (y1>y2 && x1>x2)){
					if(x1==0 && y2==0)t+=(y2f+x2f);
					else if(x1==0 && y2==n)t+=((n-y2f)+x2f);
					else if(x1==n && y2==n)t+=(n-y2f)+(n-x2f);
					else if(x1==n && y2==0)t+=((n*2)-y2f)+(n-x2f);
					else if(x1<x2 && y2<y1){
						t+=((y2f-y1f)+(x2f-x1f));
					}
				}
				else if(x1!=x2 && (y1<y2 && x1<x2)){
					if(x1==0 && y2==n)t+=(y2f-y1f);
					else if(x1==n && y2==0)t+=((n*2)-y2f)+(n-x1f);
					else if(x1==0 && y2==0)t+=(n-x2f)+(n-y1f);
					else if(x1==n && y2==n)t+=((n-x2f)+(n-y1f));
					else if(x2<x1 && y1<y2){
						t+=((y1f-y2f)+(x1f-x2f));
					}
				}
				else if(x1==x2 && x1!=0 && x1!=(n*2)){
					t=n;
				}
				if(t<min){
					min=t;
				}
			}
			x2=x1;
		}
		System.out.print(min);
	}
}

