public class HelloWorld {



    public static void main(String[] args) {

      // System.out.println(factorial(5));

        Scanner sc = new Scanner(System.in);

        int n = sc.nextInt(), m = sc.nextInt();
        int[] prices = new int[n];

        Arrays.fill(prices,0);

        for(int i = 0 ; i < n ; i ++){
            prices[i] = sc.nextInt();
        }

        for(int i = 0 ; i < n ; ++i){
            int min = findMin(prices);
            if(prices[i] >= 0)
                prices[i] = prices[i] + min;
        }

        int sum = 0;
        int count = 0;
        for(int i = 0 ; i < n ; ++i){
           if(prices[i] > 0){
               ++count;
               sum += prices[i]; 
           } 
        }

        int[] sarr = new int[count];
        int index = 0;

        for(int j = 0 ; j < n ; ++j){
            if(prices[j] > 0)
                sarr[index++] = prices[j];
        }

        Arrays.sort(sarr);

        int ans = 0;
        for(int k = 0 ; k < m ; ++k)
            ans += sarr[k];

        System.out.println(ans);

    }

    static int findMin(int[] a){
        int min = a[0];
        for(int i = 0 ; i < a.length ; ++i){
            if(min > a[i])
                min = a[i];
        }
        return min;
    }

}

