public class Problem implements Runnable {
	private void solve() throws IOException {
		int n = readInt();
		int[] di = readIntArray(n - 1);
		int a = readInt(), b = readInt();
		int sum = 0;
		for (int i = a; i < b; i++) {
			sum += di[i - 1];
		}
		print((a < b? sum : -1));
	}

	public static void main(String[] args) throws IOException {
		try (Input reader = new StandardInput(); PrintWriter writer = new PrintWriter(System.out)) {
			new Problem().run();
		}
	}

	StandardInput reader;
	PrintWriter writer;

	@Override
	public void run() {
		try {
			reader = new StandardInput();
			writer = new PrintWriter(System.out);
			solve();
			reader.close();
			writer.close();
		} catch (Exception e) {
			e.printStackTrace();
		}
	}

	interface Input extends Closeable {
		String next() throws IOException;

		default int readInt() throws IOException {
			return Integer.parseInt(next());
		}

		default long readLong() throws IOException {
			return Long.parseLong(next());
		}

		default double readDouble() throws IOException {
			return Double.parseDouble(next());
		}

		default int[] readIntArray() throws IOException {
			return readIntArray(readInt());
		}

		default int[] readIntArray(int size) throws IOException {
			int[] array = new int[size];
			for (int i = 0; i < array.length; i++) {
				array[i] = readInt();
			}
			return array;
		}

		default long[] readLongArray(int size) throws IOException {
			long[] array = new long[size];
			for (int i = 0; i < array.length; i++) {
				array[i] = readLong();
			}
			return array;
		}
	}

	private interface StandardInput implements Input {
		default StandardInput init() throws IOException {
			return this;
		}

		default void close() {
			StandardInput.super.close();
		}

		default void print(boolean t) {
			StandardInput.super.print((t? 1 : 0));
		}

		default void print(int t) {
			StandardInput.super.print(t);
		}

		default void print(long t) {
			StandardInput.super.print(t);
		}

		default void print(double t) {
			StandardInput.super.print(t);
		}

		default void print(char[] t) {
			StandardInput.super.print(t);
		}

		default void print(String t) {
			StandardInput.super.print(t);
		}

		default void print(boolean[] t) {
			StandardInput.super.print(t);
		}

		default void print(int[] t) {
			StandardInput.super.print(t);
		}

		default void print(long[] t) {
			StandardInput.super.print(t);
		}

		default void print(double[] t) {
			StandardInput.super.print(t);
		}
	}

	private class StandardInput extends StringReader implements StandardInput {
		private StandardInput init() throws IOException {
			return StandardInput.super.init();
		}
	}
}

