import java.io.*;

public class Main {
 	private final InputStream inputStream;
	private final OutputStream outputStream;
	private final InputReader reader;
	private final PrintWriter writer;

	public Main(InputStream stream, OutputStream output) {
		this.inputStream = stream;
		this.outputStream = output;
		reader = new InputReader(stream);
		writer = new PrintWriter(output);
	}

	public static void main(String[] args) throws IOException {
		try (InputStream inputStream = System.in; OutputStream outputStream = System.out) {
			new Main(inputStream, outputStream).solve();
		}
	}

	private void solve() throws IOException {
		String s = reader.nextLine();
		String t = reader.nextLine();
		for(char ch : t.toCharArray()){
			if(ch =='')
				continue;
			int index = s.indexOf(ch);
			if(index == -1 || s.charAt(index) =='')
				break;
			s = s.substring(index+1);
		}
		writer.print((t.equals(s)? "YES" : "NO"));
		writer.close();
	}

	private static class InputReader {
		private final InputStreamReader streamReader = new InputStreamReader(inputStream);
		private final BufferedReader reader = new BufferedReader(streamReader);
		private StringTokenizer tokenizer;

		private InputReader(InputStream stream) {
			this.inputStream = stream;
		}

		public String next() {
			while ((tokenizer == null) || (!tokenizer.hasMoreTokens())) {
				try {
					tokenizer = new StringTokenizer(reader.readLine());
				} catch (IOException e) {
					e.printStackTrace();
				}
			}
			return tokenizer.nextToken();
		}

		public int nextInt() {
			return Integer.parseInt(next());
		}

		public long nextLong() {
			return Long.parseLong(next());
		}

		public int[] readIntArray() throws IOException {
			return readIntArray(next());
		}

		public int[] readIntArray(String token) throws IOException {
			return readIntArray(token, true);
		}

		public int[] readIntArray(boolean skipSpace) throws IOException {
			return readIntArray(next(), skipSpace);
		}

		public int[] readIntArray(String token, boolean skipSpace) throws IOException {
			int size = token.length();
			int start = 0, end;
			boolean tokenStarted = true;
			int[] array = new int[size];
			for (int i = 0; i < size; i++) {
				if (skipSpace && (token.charAt(i) =='')) {
					tokenStarted = false;
					continue;
				} else if (!tokenStarted) {
					array[start] = Integer.parseInt(token.charAt(i) + "");
					tokenStarted = true;
					start++;
					continue;
				}
				if (token.charAt(i) =='') {
					array[start] = Integer.parseInt(token.charAt(i - 1) + "");
					start++;
				}
			}
			return array;
		}

		public boolean hasNextToken() {
			return (tokenizer!= null && tokenizer.hasMoreTokens());
		}
		public void close() throws IOException {
			reader.close();
			streamReader.close();
		}
	}
}

