    static class Reader {
        final private int BUFFER_SIZE = 1 << 16;
        private final DataInputStream din = new DataInputStream(System.in);
        private final byte[] buffer = new byte[BUFFER_SIZE];
        private int ptr = 0;
        private int buflen = 0;
        private boolean hasNextByte() {
            if (ptr < buflen) {
                return true;
            }else{
                ptr = 0;
                try {
                    buflen = din.read(buffer);
                } catch (IOException e) {
                    e.printStackTrace();
                }
                if(buflen <= 0) {
                    return false;
                }
            }
            return true;
        }
        private int readByte() {
            if(hasNextByte()) return buffer[ptr++];
            else return -1;
        }
        private static boolean isPrintableChar(int c) {
            return 33 <= c && c <= 126;
        }
        public boolean hasNext() {
            while(hasNextByte() &&!isPrintableChar(buffer[ptr])) {
                ptr++;
            }
            return hasNextByte();
        }
        public String next() {
            if(hasNext()){
                final StringBuilder sb = new StringBuilder();
                int b = readByte();
                while(isPrintableChar(b)) {
                    sb.appendCodePoint(b);
                    b = readByte();
                }
                return sb.toString();
            }else{
                return null;
            }
        }
    }

    private static String readLine() {
        final Reader reader = new Reader();
        final String res = reader.next();
        reader.close();
        return res;
    }

    private static long readLong() {
        final Reader reader = new Reader();
        final long res = Long.parseLong(reader.next());
        reader.close();
        return res;
    }

    private static int readInt() {
        final Reader reader = new Reader();
        final int res = Integer.parseInt(reader.next());
        reader.close();
        return res;
    }

    private static double readDouble() {
        final Reader reader = new Reader();
        final double res = Double.parseDouble(reader.next());
        reader.close();
        return res;
    }

    private static boolean readBoolean() {
        final Reader reader = new Reader();
        final boolean res = Boolean.parseBoolean(reader.next());
        reader.close();
        return res;
    }
}

