public class Solution {

    static long nCr(int n, int r) {
        long C[][] = new long [n+1][r+1];
        int i, j;
        int n1 = 0;
        C[0][0] = 1;

        for (i=1 ; i<=n ; i++ ) {
            n1 = n-i;
            for ( j=min(i, r) ; j>0 ; j-- ) {
                C[i][j] = C[i-1][j-1] + C[n1][j];
            }
            C[i][0] = 1;
        }
        return C[n][r];
    }

