		int n = scan.nextInt();
		int k = scan.nextInt();
		String s = scan.next();
		int min = 1000000;
		for (int i = 0; i < n - 1; i++) {
			if (s.charAt(i) == '0' && s.charAt(i + 1) == '1') {
				min = Math.min(min, n - 1 - i);
			}
		}
		for (int i = 1; i < n; i++) {
			if (s.charAt(i - 1) == '1' && s.charAt(i) == '0') {
				min = Math.min(min, i);
			}
		}
		for (int i = 1; i <= k - 2; i++) {
			if (s.charAt(i) == '0' && s.charAt(i - 1) == '1') {
				int min1 = Math.min(min, i - 1);
				int min2 = Math.min(min, n - 1 - i);
				int min3 = Math.min(min, n - 1 - (k - i));
				min = min1 + min2 + min3;
			}
		}
