     while(a!=1)
     {
       if(n%a!=0)
       {
         a--;
       }
       else 
       {
           break;
       } 
     } 
     while(b!=1)
     {
       if(n%b!=0)
       {
         b--;
       }
       else 
       {
           break;
       } 
     } 
     while(c!=1)
     {
       if(n%c!=0)
       {
         c--;
       }
       else 
       {
           break;
       } 
     } 
     long stolen1=(a*b*c)-n;
     stolen1+=n-(a*b)-(b*c)-(a*c);
     stolen1+=n-(a*b)-(b*c);
     stolen1+=n-(a*b);
     long stolen2=((a-1)*b*c)-n;
     stolen2+=(n-a*b)+(a*(b-2))+(b*c);
     stolen2+=n-a*(b-2)-b*c;
     stolen2+=n-a*(b-2);
     System.out.println(stolen1+" "+stolen2);
    }
}

