public class Solution implements Runnable {
    private void solve() throws IOException {
        long n = nextInt();
        long a = 1;
        long b = 2;
        long c = 2;
        for (long v : new long[][] { { 1, 1, 1 }, { 1, 2, 2 }, { 2, 1, 2 },
                { 2, 2, 1 }, { 3, 3, 2 }, { 3, 2, 3 }, { 2, 3, 3 } }) {
            long m = n;
            long p1 = 1, p2 = 1;
            while (m >= 0 && (p1!= v[0] || p2!= v[1])) {
                if (m > 0) {
                    m -= v[0];
                    p1 = v[0];
                }
                if (m > 0) {
                    m -= v[1];
                    p2 = v[1];
                }
            }
            if (m == 0 && p1 == v[0] && p2 == v[1]) {
                if (a * b * c > v[2]) {
                    a = v[2];
                    b = v[0];
                    c = v[1];
                } else if (a * b * c == v[2]) {
                    b = b < v[0]? b : v[0];
                    c = c < v[1]? c : v[1];
                }
            }
        }
        PrintWriter out = new PrintWriter(System.out);
        out.printf("%d %d\n", a * b * c - n, a * b * c);
        out.close();
    }

    private int nextInt() throws IOException {
        return in.nextInt();
    }

    public static void main(String[] args) throws IOException {
        try (Input in = new StandardInput()) {
            new Solution().run();
        }
    }

