import java.util.Scanner;

public class Solution {
	public static void main(String[] args) {
		Scanner sc = new Scanner(System.in);
		int n = sc.nextInt(), m = 1;
		long ans = 0;
		for(int i = 2 ; i <= n ; i++)
			for(int j = 2 ; j <= n - i ; j++){
				if(m == n) continue;
				for(int k = 2 ; k <= n - i - j ; k++){
					if(n - i - j >= 3) {
						if(m!= n) {
							if((long)(i)*j*k == n) {
								m++;
								ans += 2;
							}
							else if((long)(i)*j*k > n) {
								ans += (long)(i)*j*k - n;
								m++;
							}
							else ans += n - (long)(i)*j*k;
						}
						else ans += n - (long)(i)*j*k;
					}
				}
			}
		long t = n*n - 2*n + 2;
		if(ans == 0) ans = t;
		System.out.print(t - ans + " ");
		System.out.println(t);
	}
}

