        String[] suspect = new String[n];
        int[] person = new int[n];
        int[] result = new int[n];
        for (int i = 0; i < n; i++) {
            suspect[i] = in.next();
            if (suspect[i].charAt(0) == '+')
                person[i] = Integer.valueOf(suspect[i].charAt(1));
            else
                person[i] = 0;
            result[i] = 2;
        }
        int count = 0;
        while (count < 2) {
            int min = 9999999;
            int index = -1;
            for (int i = 0; i < n; i++) {
                if (result[i] == 2) {
                    if (person[i]!= 0 && person[person[i] - 1] == i + 1)
                        min = 1;
                    else if (person[i] == 0) {
                        int j = 0;
                        int counta = 0;
                        while (j < n) {
                            if (person[j] == i + 1) {
                                counta++;
                            }
                            j++;
                        }
                        if (counta > (n - m))
                            min = 1;
                    }
                    if (min == 1) {
                        min = 9999999;
                        index = i;
                        count++;
                    }
                }
