public class Sherlock {
    static boolean[][] g;
    static int n, m;

    static void solve()
    {
        int a, b;
        for (int i = 0; i < n; ++i)
            if (g[i][i])
            {
                System.out.print("Lie\n");
                return;
            }

        for (int i = 0; i < n; ++i)
            if (g[i][n]!= g[n][i])
            {
                a = i;
                b = n;
                break;
            }

        for (int i = 0; i < n; ++i)
            if (i!= a && g[i][n]!= g[n][i])
            {
                b = i;
                break;
            }

        for (int i = 0; i < n; ++i)
            if (g[a][i] == g[b][i])
            {
                System.out.print("Not defined\n");
                return;
            }

        System.out.print(g[a][b]? "Lie" : "Truth");
        System.out.print("\n");

        for (int i = 0; i < n; ++i)
            if (i!= a && i!= b)
                System.out.print(g[i][i]? "Truth" : "Lie");

        System.out.print("\n");
    }

    public static void main(String[] args) 
    {
        Scanner sc = new Scanner(System.in);
        n = sc.nextInt();
        m = sc.nextInt();

        g = new boolean[n][n + 1];

        for (int i = 0; i < n; ++i)
            for (int j = 0; j <= n; ++j)
                g[i][j] = false;

        for (int i = 0; i < n; ++i)
        {
            String s = sc.next();
            if (s.charAt(0) == '+')
            {
                int k = Integer.parseInt(s.charAt(1) + "");
                g[i][k] = true;
            }
            else
            {
                int k = Integer.parseInt(s.charAt(2) + "");
                g[i][k] = false;
                g[i][n] ^= true;
            }
        }

        solve();
    }
}

