public class JoeTheElusive {
    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        String s[] = br.readLine().split(" ");
        long n = Long.parseLong(s[0]);
        long m = Long.parseLong(s[1]);
        long k = Long.parseLong(s[2]);
        long diamonds[] = new long[(int) n];
        for (long i = 0; i < n; i++)
            diamonds[(int) i] = Long.parseLong(br.readLine());
        long result = 0, last[] = new long[(int) n], count = 0, temp;
        for (long j = 0; j < n; j++) {
            temp = 0;
            count = 0;
            for (long i = j; i < n; i++) {
                last[(int) i] = diamonds[(int) i];
                count += diamonds[(int) i];
                if (count > 2 * m) {
                    temp += Math.min(2 * m, count) - m;
                    count -= 2 * m - m;
                }
            }
            result = Math.max(result, temp);
            count = 0;
            for (long i = j; i >= 0; i--) {
                diamonds[(int) i] = last[(int) i];
                count += diamonds[(int) i];
                if (count > 2 * m) {
                    temp += Math.min(2 * m, count) - m;
                    count -= 2 * m - m;
                }
            }
            result = Math.max(result, temp);
        }
        k += m;
        for (long j = 0; j < n && count <= 2 * m; j++) {
            result += diamonds[(int) j];
            count += diamonds[(int) j];
            if (count > 2 * m) {
                result += m - (count - 2 * m);
                count -= 2 * m - m;
            }
        }
        System.out.println(result + k % 2);
    }
}

