import java.util.*;

public class Problem_327B {

    static int countDiv(int n) {
        int count = 0;
        for (int i = 1; i <= n; i = i*2) {
            count++;
        }
        return count;
    }

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int m = sc.nextInt();
        int a = sc.nextInt();
        int b = sc.nextInt();
        int count = 0;
        int index = 0;
        int min = 99999999;
        while (index <= n) {
            count = 0;
            int start = index;
            int temp = 0;
            while (count < m) {
                count++;
                if (start == a && temp == 0) {
                    temp++;
                    start++;
                } else if (start == b && temp == 0) {
                    temp++;
                } else if (start > b && temp == 0) {
                    temp++;
                }
                if (start > n) {
                    break;
                }
                start++;
            }
            count = count - m + 1;
            if (count < 0) {
                count = 0;
            }
            index = index + m;
            if (count < min) {
                min = count;
            }
        }
        System.out.println(min + 1);
    }
}

