public class Solution implements Runnable {

    private void solve() throws IOException {
        int n = reader.nextInt(), m = reader.nextInt(), y = reader.nextInt();

        long[] a = new long[n], b = new long[n];
        for (int i = 0; i < m; i++) {
            a[i] = reader.nextInt() - 1;
            b[i] = reader.nextInt() - 1;
        }

        long cur = 2001, mod = 1, inv = 1;
        StringBuilder sb = new StringBuilder();
        while (cur <= y) {
            mod = (cur - 1) * mod + 1;
            inv = (inv * (cur - 1)) % mod;
            cur *= n;
        }

        for (int i = 0; i < n; i++) {
            long newi = (y - 2001) * n + i;
            newi = newi % mod;
            if (newi == 0) {
                newi = mod;
            }
            newi = inv * newi % mod;
            int x = 0;
            for (; x < m; x++) {
                if (newi == a[x]) {
                    newi = b[x];
                    break;
                }
            }
            for (int j = 0; j < m; j++) {
                if (newi == a[j]) {
                    newi = b[j];
                    break;
                }
            }
            if (newi == i) {
                sb.append(newi + 1).append(' ');
            } else {
                System.out.println("The times have changed");
                return;
            }
        }
        System.out.println(sb.toString().trim());
    }

    public static void main(String[] args) throws IOException {
        try (Input reader = new StandardInput()) {
            new Solution().run();
        }
    }

    StandardInput reader;

    @Override
    public void run() {
        try {
            solve();
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    interface Input extends Closeable {
        String next() throws IOException;

        default int nextInt() throws IOException {
            return Integer.parseInt(next());
        }

        default long nextLong() throws IOException {
            return Long.parseLong(next());
        }

        default int[] readIntArray() throws IOException {
            return readIntArray(nextInt());
        }

        default int[] readIntArray(int size) throws IOException {
            int[] array = new int[size];
            for (int i = 0; i < array.length; i++) {
                array[i] = nextInt();
            }
            return array;
        }

        default long[] readLongArray(int size) throws IOException {
            long[] array = new long[size];
            for (int i = 0; i < array.length; i++) {
                array[i] = nextLong();
            }
            return array;
        }
    }

    private static class StandardInput implements Input {

        private final Scanner scanner = new Scanner(System.in);

        @Override
        public void close() throws IOException {
            scanner.close();
        }

        @Override
        public String next() throws IOException {
            return scanner.next();
        }
    }
}

