public class Solution{
	public static void main(String[] args) throws IOException {
		FileInputStream fileInputStream = new FileInputStream("input.txt");
		DataInputStream dataInputStream = new DataInputStream(fileInputStream);
		InputStreamReader inputStreamReader = new InputStreamReader(dataInputStream);
		BufferedReader reader = new BufferedReader(inputStreamReader);
		StringTokenizer token = new StringTokenizer(reader.readLine());

		int n = Integer.parseInt(token.nextToken());
		long y = Long.parseLong(token.nextToken());
		int m = Integer.parseInt(token.nextToken());
		int[][] matrix = new int[m][2];
		for(int i = 0;i<m;i++){
			token = new StringTokenizer(reader.readLine());
			matrix[i][0] = Integer.parseInt(token.nextToken());
			matrix[i][1] = Integer.parseInt(token.nextToken());
		}
		boolean found = false;
		for(long k = 1;k<=Math.pow(2, m);k++){
			int[] temp = new int[n];
			for(int j = 0;j<n;j++){
				temp[j] = j+1;
			}
			for(int p = 0;p<m;p++){
				temp[matrix[p][1]-1] = matrix[p][0]<temp[matrix[p][1]-1]?matrix[p][0]-1:temp[matrix[p][1]-1];
			}
			boolean check = true;
			for(int j = 0;j<n;j++){
				if(temp[j]>temp[j+1]){
					check = false;
					break;
				}
			}
			if(check){
				found = true;
				for(int j = 0;j<n;j++){
					System.out.print((temp[j]+1)+" ");
				}
				System.out.println();
			}
			for(int j = 0;j<m;j++){
				matrix[j][0] = matrix[j][1];
			}
			if(k==y){
				break;
			}
			matrix[m-1][1] += m;
		}
		if(!found){
			System.out.println("The times have changed");
		}
	}
}

