         for (int j = 0; j < 26; j++) {
            next[i][j] = (j == 0? i : next[i - 1][j - 1]);
         }
      }
      for (int i = 0; i <= 100; i++) {
         for (int j = 0; j < 26; j++) {
            dp[i][j] = (next[i + 1][j] - next[i][j] + 1000000007);
