public class p2 implements Runnable {
	// static variables
	private static final Scanner in = new Scanner(System.in);
	// instance variables

	private void solve() throws IOException {
		int n = in.nextInt();
		String[] s = new String[n];
		int[] rank = new int[n];
		int[] suit = new int[n];

		for (int i = 0; i < n; i++) {
			s[i] = in.next();
			rank[i] = s[i].charAt(0);
			suit[i] = s[i].charAt(1);
		}

		String result = "NO";
		for (int i = 0; i < 4; i++) {
			int rMax = 0;
			int[] temp = rank.clone();
			for (int j = 0; j < n; j++) {
				if (temp[j] > rMax) {
					rMax = temp[j];
				}
			}
			int ind = 0;
			for (int j = 0; j < n; j++) {
				if (rank[j] == rMax) {
					ind = j;
					rank[j] = 0;
					break;
				}
			}
			for (int j = 0; j < n; j++) {
				if (rank[j]!= 0) {
					if (temp[j] == temp[ind]) {
						if (suit[j] == suit[ind]) {
							result = "YES";
						}
					}
				}
			}
		}
		System.out.println(result);
	}

	@Override
	public void run() {
		try (Input in = getInput()) {
			solve();
		}
	}

	private static Input getInput() throws FileNotFoundException {
		// return new StandardInput();
		return new FileInput(new File("input.txt"));
	}

	private interface Input extends Closeable {
		String next() throws IOException;
	}

	private static class StandardInput implements Input {
		private final Scanner sc;
		StandardInput() {
			this.sc = in;
		}
		@Override
		public void close() {}
		@Override
		public String next() {return sc.next();}
	}

	private static class FileInput implements Input {
		private final Scanner sc;
		private File file;
		FileInput(final File file) throws FileNotFoundException {
			this.file = file;
			this.sc = new Scanner(file);
		}
		@Override
		public void close() {
			this.file.close();
		}
		@Override
		public String next() {
			return sc.next();
		}
	}

	public static void main(String[] args) throws IOException {
		in = new StandardInput();
		// noinspection UnusedAssignment
		try (p2 p = new p2()) {
			p.run();
		}
	}
}

