public class Solution{
    static int min(int a,int b,int c){
        if(a<b) return (b<c?a:b);
        else return (a<c?a:c);
    }
    static int max(int a,int b,int c){
        if(a>b) return (b>c?a:b);
        else return (a>c?a:c);
    }

    public static void main(String[] args){
        Scanner in = new Scanner(System.in);
        int n = in.nextInt();
        int[] can = {in.nextInt(),in.nextInt(),in.nextInt()};
        int[] cost = new int[n];
        int[] prev = new int[n];
        for(int i=0 ;i<n ; i++){
            int k = in.nextInt();
            for(int j=0 ;j<k ; j++){
                int tmp = in.nextInt();
                cost[i] = max(cost[i],cost[tmp-1]+1);
                prev[i] = min(prev[i],prev[tmp-1],max(can[0],can[1],can[2]));
            }
        }

        int[] best = new int[3];
        for(int i=0 ;i<3 ; i++){
            best[i] = max(cost[0],cost[i+1]+2,cost[i+2]+1);
        }
        System.out.println(min(min(best[0],best[1],best[2]),prev[0],prev[1])+1);
    }
}

