        }
        for(i=0; i<n; i++){
            p[i] = in.nextInt();
        }
        for(c=0; c<3; c++){
            for(i=0; i<n; i++){
                for(k=0; k<n-1; k++){
                    dp[c][1<<i] = Math.min(dp[c][1<<i], dp[c][1<<a[i][k]]+(i==0?0:k+1));
                    dp[c][1<<i] = Math.min(dp[c][1<<i], dp[c][1<<a[i][k+1]]+(i==0?0:k+1)+1);
                }
                dp[c][1<<i] = Math.min(dp[c][1<<i], dp[c^1][1<<p[i]]+(c==0?0:p[i]+1));
                p1[c][1<<i] = i==0?0:(p[i]+1);
                min = 1<<20;
                for(k=0; k<n; k++){
                    min = Math.min(min, p1[(c+k)%3][1<<a[i][k]]+((c+k+1)%3==0?0:k+1));
                }
                p1[c][1<<i] += min;
            }
        }
        System.out.println(Math.min(p1[0][1<<0], Math.min(p1[1][1<<0], p1[2][1<<0])));
    }
