public class RubikGame {


	public static void main(String [] args) throws IOException {
		InputStreamReader isr = new InputStreamReader(System.in);
		BufferedReader br = new BufferedReader(isr);

		int n = Integer.parseInt(br.readLine()), start = 1, index, min = Integer.MAX_VALUE, cost[] = new int[n],
				next[][] = new int[n][3];
		boolean[] visited = new boolean[n];
		String str[][] = new String[n][3], arr[];

		arr = br.readLine().split(" ");

		for(int i = 0 ; i < n ; i++) {
			next[i][Integer.parseInt(arr[i]) - 1] = i;
		}
		for(int i = 0 ; i < n ; i++){
			str[i] = br.readLine().split(" ");
			index = Integer.parseInt(str[i][0]) - 1;
			for(int j = 1 ; j <= 2 ; j++){
				next[i][j - 1] = Integer.parseInt(str[index][j]) - 1;
			}
		}
		cost[0] = 0;
		for(int i = 0 ; i < n ; i++){
			for(int j = 0 ; j < 3 ; j++){
				if(!visited[i]) {
					cost[i] = cost[i] + 1;
					index = next[i][j];
					visited[i] = true;
					while(!visited[index]) {
						visited[index] = true;
						cost[i] = cost[i] + 1;
						index = next[index][(j + 1) % 3];
					}
				}
			}
		}
		for(int i = 0 ; i < n ; i++) {
			if(min > cost[i]) {
				min = cost[i];
				start = i;
			}
		}
		for(int i = 0 ; i < n ; i++) {
			if(min == cost[i]) {
				cost[i] = (cost[i] + cost[start]);
			}else {
				cost[i] = (cost[i] + cost[start] - 1);
			}
		}
		System.out.println(cost[0]);
	}
}

