        int n = sc.nextInt(), c = 0; int a[] = new int[n]; int r; 
        for(int i = 0 ;i<n;i++) a[i] = sc.nextInt();
        for(int j = n-2 ; j >= 0 ;j++){
            r = j;
            while(a[r+1] > a[r]) r++;
            if(a[r] > a[j]){
                c += r-j+1;
                a[r] = a[j] + a[r+1];
