        if(mem[x-1]!= 0) mem[x-1] = 0;
        else out.print("ILLEGAL_ERASE_ARGUMENT\n");
      } else if(op.equals("defragment")){
        for(int i = 0; i <= M; i++){
          if(mem[i]!= 0){
            for(int j = 0; j < i; j++){
              if(mem[j] == 0){
                mem[j] = mem[i];
                mem[i] = 0;
                break;
              }
            }
          }
        }
      } else out.print("ILLEGAL_COMMAND\n");
    }
  }

