public class Screens {
    static class Bound{
        long x;
        long y;
        public Bound(long x, long y){
            this.x=x;
            this.y=y;
        }
        boolean is(long x, long y){
            return x<=this.x && y<=this.y;
        }
    }
    public static void main(String[] args) {
        Scanner in=new Scanner(System.in);
        long a = in.nextInt();
        long b = in.nextInt();
        long x = in.nextInt();
        long y = in.nextInt();
        Bound ab = new Bound(a,b);
        Bound xy = new Bound(x,y);
        Bound max=new Bound(0,0);
        for (long b1 = 0 ; b1 < a ; b1++){
            if(b1>0 && a%b1==0)
            {
                long a1 = a/b1;
                if(xy.is(a1,b1))
                {
                    max = new Bound(a1,b1);
                }
                else if(xy.is(b1,a1))
                {
                    max = new Bound(b1,a1);
                }
            }
        }
        if(max.x>0 && max.y>0){
            for (long a1 = 0 ; a1 < max.x ; a1++){
                if(max.x%a1==0){
                    long b1 = max.x/a1;
                    if(ab.is(a1,b1)){
                        System.out.println(a1+" "+b1);
                        break;
                    }
                }
            }
        }
        else
        {
            System.out.println("0 0");
        }
    }
}

