public class Solution {

    static int n;
    static int m;
    static char [][] c;
    static boolean [][] use;
    static boolean [][] v;
    static int [][] count;
    static int [][] dp;

    static void solve(int x, int y){
        int xx = (n - x + 1);
        int yy = (m - y + 1);

        if(x > n || y > m){
            return;
        }

        for(int i = 0 ; i < xx ; i++){
            for(int j = 0 ; j < yy ; j++){
                use[i][j] = true;
            }
        }

        for(int i = 0 ; i < xx ; i++){
            for(int j = 0 ; j < yy ; j++){
                if(c[i][j]!= c[x + i][y + j]){
                    use[i][j] = false;
                }
            }
        }

        for(int i = 1 ; i < xx ; i++){
            for(int j = 1 ; j < yy ; j++){
                if(use[i][j]){
                    v[i][j] = true;
                    count[i][j] = count[i - 1][j - 1] + 1;
                }else{
                    v[i][j] = false;
                    count[i][j] = 0;
                }
            }
        }

        for(int i = 0 ; i < xx ; i++){
            for(int j = 0 ; j < yy ; j++){
                if(use[i][j]){
                    dp[i][j] = Math.min(count[i][j], Math.min(dp[i - 1][j], dp[i][j - 1]));
                }else{
                    dp[i][j] = 0;
                }
            }
        }

        dp[xx - 1][yy - 1] = (x * y) / ((n / x) * (m / y));
        if(dp[xx - 1][yy - 1] * x == n && dp[xx - 1][yy - 1] * y == m){
            System.out.print(1 + " ");
            System.out.print((x) + " ");
            System.out.print((y) + "\n");
        }
    }

    public static void main(String[] args){
        Scanner in = new Scanner(System.in);
        n = in.nextInt();
        m = in.nextInt();
        c = new char [n][m];
        use = new boolean [n][m];
        v = new boolean [n][m];
        count = new int [n][m];
        dp = new int [n][m];
        int max = 0;
        int min = 100;

        for(int i = 0 ; i < n ; i++){
            c[i] = in.next().toCharArray();
        }

        for(int y = 1 ; y <= Math.min(n, m) / 2 ; y++){
            for(int x = 1 ; x < y ; x++){
                solve(x, y);
            }
            for(int x = 0 ; x < n - y + 1 ; x++){
                solve(x, y);
            }
            for(int x = n - y + 1 ; x > 0 ; x--){
                solve(x, y);
            }
        }
        System.out.print((dp[0][0]) + " ");
        System.out.print((n) + " ");
        System.out.print((m) + "\n");
    }
}

