    public static String readString(StreamTokenizer in) throws IOException {
        String res = "";
        in.nextToken();
        if(in.ttype!= StreamTokenizer.TT_EOL){
            res = res + (char)in.ttype;
            while(in.nextToken()!= StreamTokenizer.TT_EOL){
                res += (char)in.ttype;
            }
        }
        return res;
    }
    public static int readInt(StreamTokenizer in) throws IOException {
        in.nextToken();
        int res = 0;
