class ConvexHull {
    private double[] X = new double[100000];
    private double[] Y = new double[100000];
    private int point = 3;
    private double d;
    private int v0, v1, v2;

    public ConvexHull() {
        X[0] = 0;
        Y[0] = 0;
        X[1] = 2;
        Y[1] = 0;
        X[2] = 1;
        Y[2] = 1;
        v0 = 0;
        v1 = 1;
        v2 = 2;
    }

    public boolean isIn(int x, int y) {
        d = (Y[v2] - Y[v0]) * (x - X[v0]) - (X[v2] - X[v0]) * (y - Y[v0]);
        if (((Y[v1] - Y[v0]) * (x - X[v0]) - (X[v1] - X[v0]) * (y - Y[v0])) > 0) {
            d = -1 - d;
        }
        return (d <= 0);
    }

    public void addPoint(int x, int y) {
        X[point] = x;
        Y[point] = y;
        while (point > 2 && orientation(point - 2, point - 1, point) >= 0) {
            v2 = point - 1;
            v1 = point - 2;
            v0 = point - 3;
            point--;
        }
        point++;
    }

    private int orientation(int v, int v1, int v2) {
        return (int) (((X[v2] - X[v1]) * (Y[v] - Y[v1])) - ((Y[v2] - Y[v1]) * (X[v] - X[v1])));
    }
}

