    private static class Point implements Comparable<Point> {
        int x, y;

        Point (int x, int y) {
            this.x = x;
            this.y = y;
        }

        public int compareTo (Point point) {
            if (x!= point.x) return x - point.x;
            else return y - point.y;
        }

        public String toString () {
            return ("(" + x + "," + y + ")");
        }
    }

    private static class ConvexHull {
        public static int isIn (Point[] points, int l, int m, int r, Point testPoint) {
            if (doIntersect(points, l, m, r) || points[l].compareTo(points[m])!= points[r].compareTo(testPoint)) {
                return orientation(points[l], points[m], testPoint)!= -1;
            } else {
                return onSegment(points[l], points[m], testPoint);
            }
        }

        private static int orientation (Point p1, Point p2, Point p3) {
            int val = (p2.y - p1.y) * (p3.x - p2.x) - (p2.x - p1.x) * (p3.y - p2.y);
            if (val == 0) return 0;
            return (val > 0)? 1 : 2;
        }

        private static boolean doIntersect (Point[] points, int l, int m, int r) {
            // if not on same line
            if (orientation(points[l], points[m], points[r])!= 0) return false;
            // if p3 is on the extended side of p1 and p2
            if (orientation(points[l], points[m], points[l]) == 2 && orientation(points[m], points[r], points[l]) == 2) return true;
            // if p3 is on the extended side of p2 and p1
            if (orientation(points[m], points[r], points[m]) == 2 && orientation(points[r], points[l], points[m]) == 2) return true;
            return false;
        }

        private static boolean onSegment (Point p1, Point p2, Point p3) {
            if (p1.x <= p3.x && p3.x <= p2.x || p2.x <= p3.x && p3.x <= p1.x) return p3.y >= Math.min(p1.y, p2.y) && p3.y <= Math.max(p1.y, p2.y);
            else return false;
        }
    }
