		dist = new int[n];
		for (int i = 0; i < m; i++) {
			graph[i][0] = stdin.nextInt() - 1;
			graph[i][1] = stdin.nextInt() - 1;
		}
	}

	private static void find() {
		for (int i = 0; i < n; i++) {
			boolean[] visited = new boolean[n];
			Arrays.fill(visited, false);
			min = Integer.MAX_VALUE;
			dfs(i, visited);
		}
	}

	private static void dfs(int x, boolean[] visited) {
		visited[x] = true;
		for (int i = 0; i < 2; i++) {
			int v = graph[x][i];
			if (visited[v]) {
				if (dist[v] == dist[x] + 1) {
