		reader.close();
	}

	public void mark(int readAheadLimit) throws IOException {
		reader.mark(readAheadLimit);
	}

	public void reset() throws IOException {
		reader.reset();
	}

	private interface Reader {
		String next() throws IOException;

		default void mark(int readAheadLimit) throws IOException {
		}

		default void reset() throws IOException {
		}

		default void close() throws IOException {
		}
	}

	private static class StandardInput implements Reader {
		private final StandardInput reader = this;
		private final BufferedReader buffer = new BufferedReader(new InputStreamReader(System.in));

		private StringTokenizer stringTokenizer;

		@Override
		public String next() throws IOException {
			while ((stringTokenizer == null ||!stringTokenizer.hasMoreTokens()) && reader.ready()) {
				stringTokenizer = new StringTokenizer(reader.next());
			}
			if (stringTokenizer == null)
				return null;
			return stringTokenizer.nextToken();
		}
	}
}
