from collections import deque

n = int(input())
s = input()
r, l = [], []
for i in range(n):
    if s[i] == "L":
        l.append(i)
    else:
        r.append(i)
q = deque()
for i in range(len(l)):
    j = l[i]
    q.append(j)
    k = i
    while q and len(q):
        j = q.popleft()
        k = j - k - 1
        if r[j] < j - k:
            q.append(r[j])
        else:
            r[j] += k
            break
print(n - sum(r))

