from sys import stdin


class UnionFind:
    def __init__(self, n):
        self.n = n
        self.parents = [-1] * n

    def find(self, x):
        if self.parents[x] < 0:
            return x
        else:
            self.parents[x] = self.find(self.parents[x])
            return self.parents[x]

    def union(self, x, y):
        x, y = self.find(x), self.find(y)
        if x == y:
            return
        if self.parents[x] > self.parents[y]:
            x, y = y, x
        self.parents[x] += self.parents[y]
        self.parents[y] = x


def main():
    stdin = stdin.buffer.read().decode('utf-8')
    stu, lace = map(int, stdin.split())
    uf = UnionFind(stu)
    for _ in range(lace):
        a, b = map(int, stdin.split())
        uf.union(a, b)
    print(stu - uf.n)


if __name__ == '__main__':
    main()

