import bisect


def get_positions():
    x_min = float("inf")
    x_max = -float("inf")
    y_min = float("inf")
    y_max = -float("inf")

    result = []
    for _ in range(int(input())):
        position = [int(x) for x in input().split(" ")]
        result.append(position)
        x_min = min(x_min, position[0])
        x_max = max(x_max, position[0])
        y_min = min(y_min, position[1])
        y_max = max(y_max, position[1])

    return (result, x_min, x_max, y_min, y_max)


def add_to_grid(positions):
    grid = []
    for x in range(positions[1], positions[2] + 1):
        grid.append([])
        for y in range(positions[3], positions[4] + 1):
            grid[x - positions[1]].append(0)

    for position in positions[0]:
        x = position[0] - positions[1]
        y = position[1] - positions[3]

        grid[x][y] += 1

    return grid


def find_solution(grid):
    result = 0
    for x in range(len(grid)):
        pos_y = []
        for y in range(len(grid[x])):
            pos_y.append(grid[x][y])

        if len(pos_y) < x:
            result += x - len(pos_y)

    return result


def solve(grid):
    for i in range(1, len(grid)):
        for j in range(len(grid[i])):
            grid[i][j] += grid[i - 1][j] + grid[i][j - 1] - grid[i - 1][j - 1]

    return grid


def main():
    positions, x_min, x_max, y_min, y_max = get_positions()
    grid = add_to_grid(positions)
    grid = solve(grid)
    result = find_solution(grid)
    print(result)


if __name__ == '__main__':
    main()

