def pf(n):
    a=[2]
    for i in range(3,int(math.sqrt(n))+1,2):
        if n%i==0:
            a.append(i)
            j=n//i
            while(j%i==0):
                j=j//i
            a.append(j)
    return a

def pow(a,b,c):
    res=1
    x=a%c
    x=x**(b//c)%c
    res=(res*x)%c
    return res

def check(a,k):
    for i in a:
        if k%i==0:
            return True
    return False

def calc_inv_mod(a,k):
    if a%k==0:
        return k-1
    else:
        s=(k-2)//a
        return s*pow(a,s,k)


