                m[i]=1
    a=[]
    mx=0
    for i in range(n):
        a.append(pf(m[i]!=-1 and m[i] or 1))
        mx=max(mx,m[i]!=-1 and m[i] or 1)
    inv=[]
    for i in range(1,len(mx)+1):
        if check(a,i):
            inv.append(i)
    ans=0
    for i in range(len(a)):
        if len(inv)>=i:
            ans+=(len(a)-i)*calc_inv_mod(mx,inv[i])
    print(ans)

