import sys

input = sys.stdin.buffer.readline


def read_image(N=5):
    rows = [list(input()) for _ in range(N)]
    return rows


def next_block(cur_block, i, j, move, image):
    cur_block += move
    while cur_block >= 0 and cur_block < len(image[i]) and image[i][cur_block] == '0':
        cur_block += move
    return cur_block


if __name__ == '__main__':
    M, N = map(int, input().split())
    rows = read_image(M)
    steps = N % M
    if steps:
        rows += [list(input()) for _ in range(M-steps)]
    cur_block = 0
    while 0 <= cur_block < M:
        for _ in range(steps):
            cur_block += 1
            if cur_block == M:
                cur_block = 0
                break
        i = 0
        cur_block = next_block(cur_block, i, cur_block, +1, rows)
        cur_block = next_block(cur_block, i, cur_block, -1, rows)
        if not cur_block < M:
            break
        cur_block += M

    print(rows[cur_block][0])

from collections import deque
import sys


def solve():
    dp, cp = deque(), deque()
    for line in sys.stdin:
        for ch in line.strip():
            if ch!= '0':
                dp.append(ch)
    while len(dp):
        if not cp:
            ch = dp.popleft()
            dp.rotate(-1)
            cp.append(ch)
        elif dp[0] == cp[0]:
            dp.popleft()
            cp.popleft()
        else:
            dp.rotate(-1)
            cp.rotate(1)
    print(dp[0])


if __name__ == '__main__':
    solve()

