def cross(A, B):
  return A[0]*B[1] - A[1]*B[0]

def dot(A, B):
  return A[0]*B[0] + A[1]*B[1]

def onSeg(A, B, P):
  #  return (cross(P-A, B-A) == 0) and (cross(P-A, A-B) <= 0) and (cross(P-B, B-A) <= 0)
  return (cross(P-A, B-A) == 0) and ((dot(P-A, B-A) >= 0) and (dot(P-A, A-B) <= 0)) and ((dot(P-B, B-A) >= 0) and (dot(P-B, A-B) <= 0))

def check(AB, V):
  A, B = AB
  l = len(V)
  for i in range(l):
    if onSeg(A, B, V[i]):
      return True
  return False

def isinside(AB, V):
  A, B = AB
  x, y = A
  p1, p2 = V[0], V[1]
  s, flag = 0, 0
  for i in range(2, len(V)):
    p, q = V[i-1], V[i]
    if onSeg(p, q, (x,y)):
      s += flag
    if cross(p2 - p1, p - p1) > 0:
      flag = -flag
  return s > 0

