# Manejando horarios quebrados

def mover_cadena(cadena, posiciones):
    for i in posiciones:
        cadena = cadena[:i] + cadena[i+1] + cadena[i] + cadena[i+2:]
    return cadena

def mover_unos(cadena, posiciones):
    for i in posiciones:
        if cadena[i] == '0':
            cadena = cadena[:i] + '1' + cadena[i+1:]
        else:
            cadena = cadena[:i] + '0' + cadena[i+1:]
    return cadena

