def func(s,n):
    for i in range(n):
        s[i] = set(s[i])
        s[i] = sorted(list(s[i]))

        for j in s[i]:
            s[i].remove(j)

    for i in range(len(s)):
        for j in range(len(s)-i-1):
            if s[j] == s[j+1]:
                s[j] = s[j]|s[j+1]
                s.pop(j+1)
                break

    ans = ''
    for i in range(n):
        for j in range(len(s[i])):
            ans += s[i][j]

    return ans

