def gcd(a, b):
    if b == 0:
        return a
    return gcd(b, a % b)
def check(x, n):
    cur = 1
    x -= 1
    for i in range(1, n + 1):
        cur += i * i
    return (x >= cur)

def solve(n, x, y):
    res = 0
    if n == 1:
        if check(x, y):
            return [y - 1]
        else:
            return [-1]
    ans = []
    d = x - n + 1
    if d < 0:
        return [-1]
    for i in range(0, n):
        t = min(gcd(d, x), y - n + 1)
        ans.append(t)
        d = (d - t) // gcd(d, x)
    return ans

