def ints():
    return [int(x) for x in input().split(' ')]

def solve(n, friends):
    n_set = set(range(n))

    ok = {0: True}
    for f in friends:
        f = frozenset(f)
        if f in ok:
            continue

        not_f = n_set - f

        new_ok = {}
        for b, v in ok.items():
