class UnionFind:
    def __init__(self, num_elements):
        self.parents = list(range(num_elements))
        self.ranks = [0] * num_elements

    def union(self, a, b):
        a_root = self.find(a)
        b_root = self.find(b)
        if a_root == b_root:
            return
        if self.ranks[a_root] > self.ranks[b_root]:
            self.parents[b_root] = a_root
        elif self.ranks[b_root] > self.ranks[a_root]:
            self.parents[a_root] = b_root
        else:
            self.parents[a_root] = b_root
            self.ranks[b_root] += 1

    def find(self, a):
        parent = self.parents[a]
        if parent == a:
            return parent
        else:
            return self.find(parent)

