from itertools import accumulate
from bisect import bisect_right


def solve(n, l, arr):
    arr.sort()
    acc = accumulate(arr)

    lo = 0
    hi = 10 ** 6 + 5

    while hi - lo > 1:
        mid = (lo + hi) // 2
        sum = 0

        for i in range(n):
            sum += bisect_right(arr, mid / l + 1e-6) - i

        if sum < mid * l:
            lo = mid
        else:
            hi = mid

    sum = 0
    for i in range(n):
        sum += bisect_right(arr, hi / l + 1e-6) - i

    ans = 0
    for i in range(n):
        k = min(sum, hi)
        ans = max(ans, k * (acc[n - 1 - i] - acc[i]))
        sum -= bisect_right(arr, k / l + 1e-6) - i

    return ans


def main():
    n, l = map(int, input().split())
    arr = list(map(int, input().split()))
    ans = solve(n, l, arr)
    print(ans)


