def f(n, b, c):
	ans = 0
	for i in range(0, n + 1):
		cur = (pow(b, n) - pow(b, i)) / (b - 1)
		l = (c + cur - 1) / cur
		if l < n + 1 - i:
			ans += (c + cur - 1) % cur
		elif l == n + 1 - i:
			ans += (c + cur - 1) % cur + 1
		else:
			ans += (n + 1 - i) * cur
		if ans >= c * (n + 1):
			return ans - c * (n + 1) + 1
		#print("i: " + str(i) + ", l: " + str(l) + ", cur: " + str(cur))
	return ans



