class Solution:
    def __init__(self):
        pass

    def solve(self):
        s = read_str()
        # hostname
        # username
        # resource
        parts = s.split('@')
        if len(parts) == 1:
            print("NO")
            return
        hostname = parts[0]
        s = parts[1]
        parts = s.split('/')
        if len(parts) == 1:
            username = parts[0]
            print("YES" if self.check_username(username) and self.check_hostname(hostname) else "NO")
        else:
            username = parts[0]
            resource = parts[1]
            print("YES" if self.check_username(username) and self.check_hostname(hostname) and self.check_resource(resource) else "NO")

    def check_resource(self, resource):
        return len(resource) <= 16 and resource.isalnum()

    def check_username(self, username):
        return len(username) <= 16 and username.isalnum()

    def check_hostname(self, hostname):
        parts = hostname.split('.')
        for part in parts:
            if not self.check_username(part):
                return False
        return True


