    counter = Counter(chain.from_iterable([set('aeiou'), s]))
    res = 0

    for i in range(24):
        vowels_set = set()
        cnt = 0
        for elem, val in counter.items():
            cnt += val
            if val == 1:
                vowels_set.add(elem)

        if cnt % 3 == 0:
            curr_pow = 2 ** i
            for j in range(i):
                if (1 << j) & curr_pow:
                    res ^= counter[vowels_set.pop()] ** 2
                    res ^= counter[vowels_set.pop()] ** 2
                    res ^= counter[vowels_set.pop()] ** 2
        else:
            curr_pow = 2 ** i
            for j in range(i):
                if (1 << j) & curr_pow:
                    res ^= counter[vowels_set.pop()] ** 2
                    res ^= counter[vowels_set.pop()] ** 2

    return res


