def length(n):
    return int(math.floor(math.log10(10**n)))

def findpos(n,pos):
    #Find index of digit n in the number with n+1 digits, starting at the left. Return this position.
    digit_len = length(n)
    place_value = 1
    while place_value <= pos:
        pos -= place_value*digit_len
        place_value*=10
        digit_len-=1
    digit_num = n+1 - digit_len
    if digit_num == pos+1:
        return n
    elif digit_num < pos+1:
        return n+1
    else:
        pos += digit_num
        return place_value - 1 + findpos(n-digit_num,pos)

