def solve(arr):
    # Find increasing subsequence
    dic = {1:-1}
    dic[0] = 0
    seq = [1]
    for x in arr:
        if x not in dic:
            dic[x] = len(seq)
            seq.append(x)
    seq = [seq[i] for i in sorted(dic)]
    # Count the number of increasing subsequences of length i
    dic = {}
    for v in reversed(seq):
        dic[v] = dic.get(v-1, 0) + 1
    ans = 0
    for v in arr:
        k = len(dic) - dic[v]
        ans = max(ans, k)
    return ans


