def primes(n):
    """ Returns  a list of primes < n """
    sieve = [True] * n
    for i in range(3,int(n**0.5)+1,2):
        if sieve[i]:
            sieve[i*i::2*i] = [False] * ((n-i*i-1)//(2*i)+1)
    return [2] + [i for i in range(3,n,2) if sieve[i]]


primeList = primes(2 * 100000)


def getFactor(n):
    i = 0
    fact = []
    while primeList[i] <= n:
        e = 0
        while n % primeList[i] == 0:
            n //= primeList[i]
            e += 1
        if e > 0:
            fact.append(e)
        i += 1
    return fact


