def prime_factorize(n):
    p = 2
    res = {}
    while p*p <= n:
        while n % p == 0:
            n //= p
            res[p] = res.get(p, 0) + 1
        p += 1 + p & 1
        if p * p > n:
            res[n] = 1
            break
    return res

